$(document).ready(function()
{
    $("#denmark").hide();
    $("#norway").hide();
    $("#sweden").hide();
    $("#denmark-zip").hide();
    $("#norway-zip").hide(); 
    $("#sweden-zip").hide();
});

$( "#iceland" ).select(function() {
    if($("#denmark").hasClass("active") == true)
    {
        $("#denmark").removeClass("active");
        $("#iceland").addClass("active");
        $( "#iceland-zip, #denmark-zip" ).toggle();
    }
    else if($("#norway").hasClass("active") == true)
    {
        $("#norway").removeClass("active");
        $("#iceland").addClass("active");
        $( "#norway-zip, #iceland-zip" ).toggle();
    }
    else if($("#sweden").hasClass("active") == true)
    {
        $("#sweden").removeClass("active");
        $("#iceland").addClass("active");
        $( "#sweden-zip, #iceland-zip" ).toggle();
    }
});
